import { useEffect, useReducer } from 'react';
//import MediaRecorderPolyfill from 'audio-recorder-polyfill';
//window.MediaRecorder = MediaRecorderPolyfill;

type ReturnedSig = {
  recorder: MediaRecorder | null;
  start: () => void;
  stop: () => void;
  isRecording: boolean;
};

type State = {
  isRecording: boolean,
  recorder: MediaRecorder | null,
  data: Blob | null
}

type Actions =
  | {type: 'start'}
  | {type: 'startRecording', payload: {recorder: MediaRecorder}}
  | {type: 'dataReceived', payload: {data: Blob}}
  | {type: 'stop'};

const initState: State = {
  isRecording: false,
  recorder: null,
  data: null
};

const reducer = (state: State, action: Actions): State => {
  switch (action.type) {
    case 'start':
      return {...state, isRecording: true};
    case 'stop':
      return {...state, isRecording: false};
    case 'startRecording':
      return {...state, isRecording: true, recorder: action.payload.recorder};
    case 'dataReceived':
      return {...state, isRecording: false, data: action.payload.data};
    default:
      return state;
  }
};

export const useVoiceRecorder = (callbackFn: (result: Blob) => void): ReturnedSig => {
  const [state, dispatch] = useReducer(reducer, initState);

  const finishRecording = ({data}: {data: Blob}) => {
    dispatch({type: 'dataReceived', payload: {data}});
    callbackFn(state.data);
  };

  const start = () => {
    dispatch({type: 'start'});
    navigator.mediaDevices.getUserMedia({audio: true}).then(stream => {
      const recorder = new MediaRecorder(stream);
      dispatch({type: 'startRecording', payload: {recorder}});
      recorder.start();

      recorder.addEventListener('dataavailable', finishRecording);
    });
  };

  const stop = () => {
    const recorder = state.recorder;
    dispatch({type: 'stop'});
    if (recorder) {
      state.recorder.stop();
      recorder.removeEventListener('dataavailable', finishRecording);
    }
  };

  return {
    start,
    stop,
    recorder: state.recorder,
    isRecording: state.isRecording,
  };
};
